(63, 84, 'Pavo Blog', 'pavoblog', 'pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Pavo Blog</name>\n	<code>pavoblog</code>\n	<version>1.0.0</version>\n	<author>pavothemes</author>\n	<link>http://www.pavothemes.com</link>\n    <file path=\"admin/controller/common/column_left.php\">\n        <operation>\n            <search><![CDATA[// Catalog]]></search>\n            <add position=\"before\"><![CDATA[\n                // pavoblog language file\n                $this->load->language(\'extension/module/pavoblog\');\n\n                // PavoBlog Settings menu\n                $settings = array();\n\n                // access posts\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/posts\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_posts_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/index\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // modify posts\n                if ( $this->user->hasPermission( \'modify\', \'extension/module/pavoblog/post\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_post_add_new_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/post\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // access categories\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/categories\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_categories_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/categories\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // access comments\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/comments\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_comments_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/comments\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n                // view settings panel\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/settings\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_settings_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/settings\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                if ( $settings ) {\n                    $data[\'menus\'][] = array(\n                        \'id\'       => \'pavoblog\',\n                        \'icon\'     => \'fa fa-tasks fw\',\n                        \'name\'     => $this->language->get( \'heading_title\' ),\n                        \'href\'     => \'\',\n                        \'children\' => $settings\n                    );\n                }\n                // End PavoBlog Settings menu\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation>\n            <search>\n                <![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'));]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'), $this->registry->get(\'config\')->get(\'config_theme\'));]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"system/library/template.php\">\n        <operation>\n            <search>\n                <![CDATA[public function __construct($adaptor) {]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[public function __construct($adaptor, $theme = \'default\' ){\n                    $this->theme = $theme;\n                ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[$this->adaptor = new $class();]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[$this->adaptor = new $class( $this->theme );]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"system/library/template/twig.php\">\n        <operation>\n            <search>\n                <![CDATA[public function __construct() {]]>\n            </search>\n            <add position=\"replace\"><![CDATA[public function __construct( $theme = \'default\' ) {\n                    $this->theme = $theme;\n                ]]></add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[$config = array(\'autoescape\' => false);]]>\n            </search>\n            <add position=\"before\">\n            <![CDATA[\n                if ( ! defined( \'DIR_CATALOG\' ) ) {\n                    $dirs = array(\n                        DIR_TEMPLATE . $this->theme . \'/template/\',\n                        DIR_TEMPLATE . \'default/template/\'\n                    );\n                    foreach ( $dirs as $dir ) {\n                        if ( is_dir( $dir ) ) {\n                            $loader->addPath( $dir );\n                        }\n                    }\n                }\n                ]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/startup/seo_url.php\">\n        <operation>\n            <search>\n                <![CDATA[$this->request->get[\'route\'] = $query->row[\'query\'];]]>\n            </search>\n            <add position=\"replace\"><![CDATA[\n                    if ( $url[0] === \'pavo_cat_id\' ) {\n                        // archive\n                        $this->request->get[\'route\'] = \'extension/pavoblog/archive\';\n                        $this->request->get[\'pavo_cat_id\'] = $url[1];\n                    } else if ( $url[0] === \'pavo_post_id\' ) {\n                        // single\n                        $this->request->get[\'route\'] = \'extension/pavoblog/single\';\n                        $this->request->get[\'pavo_post_id\'] = $url[1];\n                    } else if ( $url[0] === \'extension/pavoblog/archive/author\' && ! empty( $parts[1] ) ) {\n                        $author = $parts[1];\n                        $query = $this->db->query( \"SELECT * FROM \" . DB_PREFIX . \"user WHERE username = \'\".$this->db->escape( $author ).\"\'\" );\n                        $user = $query->row;\n                        if ( ! empty( $user[\'user_id\'] ) ) {\n                            $this->request->get[\'route\'] = \'extension/pavoblog/archive\';\n                            $this->request->get[\'pavo_username\'] = $user[\'username\'];\n                        }\n                        break;\n                    } else {\n                        $this->request->get[\'route\'] = $query->row[\'query\'];\n                    }\n                ]]></add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[} elseif ($key == \'path\') {]]>\n            </search>\n            <add position=\"replace\"><![CDATA[\n                    } else if ( strpos( $data[\'route\'], \'extension/pavoblog/\' ) === 0 ) {\n\n                        if ( $data[\'route\'] === \'extension/pavoblog/archive\' ) {\n                            if ( $key === \'route\' ) {\n                                $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                    $url .= \'/\' . $query->row[\'keyword\'];\n                                    unset($data[$key]);\n                                }\n                                if ( isset( $data[\'pavo_cat_id\'] ) ) {\n                                    $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape(\'pavo_cat_id=\' . (int)$data[\'pavo_cat_id\']) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                    if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                        $url .= \'/\' . $query->row[\'keyword\'];\n                                        unset($data[\'pavo_cat_id\']);\n                                    }\n                                }\n                            }\n                        } else if ( $data[\'route\'] === \'extension/pavoblog/archive/author\' ) {\n\n                            if ( $key === \'pavo_username\' ) {\n                                $query = $this->db->query( \"SELECT * FROM \" . DB_PREFIX . \"user WHERE username = \'\".$this->db->escape( $value ).\"\'\" );\n                                $user = $query->row;\n                                if ( ! empty( $user[\'user_id\'] ) ) {\n                                    $url .= \'/\' . $user[\'username\'];\n                                }\n                                unset($data[$key]);\n                            } else if ( $key === \'route\' ) {\n                                $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                    $url .= \'/\' . $query->row[\'keyword\'];\n                                }\n                            }\n                        } else if ( $data[\'route\'] === \'extension/pavoblog/single\' && $key === \'pavo_post_id\' ) {\n                            $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($key . \'=\' . (int)$value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                            if ( $query->num_rows && $query->row[\'keyword\'] ) {\n                                $url .= \'/\' . $query->row[\'keyword\'];\n                                unset($data[$key]);\n                            }\n                        }\n                    } elseif ($key == \'path\') {\n                ]]></add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search>\n                <![CDATA[$data[\'telephone\'] = $this->config->get(\'config_telephone\');]]>\n            </search>\n            <add position=\"after\"><![CDATA[\n                $data[\'facebook_app_id\'] = $this->config->get(\'pavoblog_facebook_appid\');\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"catalog/view/theme/*/template/common/header.twig\">\n        <operation>\n            <search>\n                <![CDATA[<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">]]>\n            </search>\n            <add position=\"after\"><![CDATA[\n                <meta property=\"fb:app_id\" content=\"{{ facebook_app_id }}\"/>\n            ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2017-12-08 10:29:08'),
(61, 82, 'PavoBuilder', 'pavobuilder', 'pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n    <name>PavoBuilder</name>\n    <code>pavobuilder</code>\n    <version>1.0.0</version>\n    <author>pavothemes</author>\n    <link>http://www.pavothemes.com</link>\n    <file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search><![CDATA[$data[\'title\'] = $this->document->getTitle();]]></search>\n            <add position=\"before\"><![CDATA[\n\n            $this->document->addStyle( \'catalog/view/javascript/pavobuilder/dist/pavobuilder.min.css\' );\n            $this->document->addScript( \'catalog/view/javascript/pavobuilder/dist/pavobuilder.min.js\' );\n        ]]></add>\n        </operation>\n    </file>\n</modification>', 1, '2017-12-08 10:21:01'),
(35, 56, 'Pavo Newsletter', 'pavnewsletter', 'Pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Pavo Newsletter</name>\n	<code>pavnewsletter</code>\n	<version>1.0.0</version>\n	<author>Pavothemes</author>\n	<link>http://www.pavothemes.com</link>\n</modification>', 1, '2017-11-21 16:33:14'),
(68, 89, 'Simtech', 'Simtech', 'pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Simtech</name>\n	<code>Simtech</code>\n	<version>1.0.0</version>\n	<author>pavothemes</author>\n	<link>http://www.pavothemes.com</link>\n    <file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search><![CDATA[return $this->load->view(\'common/header\', $data);]]></search>\n            <add position=\"replace\"><![CDATA[\n        $data[\'body_class\'] = $this->getBodyClass();\n        // pavothemer header layout file\n        $data[\'pavothemer_theme_width\'] = $this->config->get( \'pavothemer_theme_width\' );\n        $data[\'pavothemer_layout\']      = $this->config->get( \'pavothemer_layout\' );\n        $header = $this->config->get( \'pavothemer_header_layout\' );\n\n        $file = DIR_TEMPLATE . $theme . \'/template/common/\' . $header . \'.twig\';\n        if ( $header && file_exists( $file ) ) {\n            return $this->load->view(\'common/\' . $header, $data);\n        } else {\n            return $this->load->view(\'common/header\', $data);\n        }\n\n    }\n\n    \n            ]]></add>\n        </operation>\n    </file>\n\n</modification>', 1, '2017-12-22 17:00:59'),
(69, 90, 'Simtech_Skin', 'Simtech_Skin', 'pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Simtech_Skin</name>\n	<code>Simtech_Skin</code>\n	<version>1.0.0</version>\n	<author>pavothemes</author>\n	<link>http://www.pavothemes.com</link>\n    <file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search><![CDATA[$data[\'title\'] = $this->document->getTitle();]]></search>\n            <add position=\"before\"><![CDATA[\n                // theme\n                $theme = $this->config->get( \'config_theme\' );\n                // skin\n                $skin = $this->config->get( \'pavothemer_default_skin\' );\n                /**\n                * style files\n                *\n                * stylesheet file\n                * stylesheet-rtl file\n                * skin file\n                */\n                $styles = array(\n                \'stylesheet\',\n                $this->language->get( \'direction\' ) === \'rtl\' ? \'stylesheet-rtl\' : \'\',\n                \'animate\',\n                $skin ? \'skins/\' . $skin : \'\',\n                \'customize\'\n                );\n\n                $this->document->addStyle( \'catalog/view/javascript/pavobuilder/dist/pavobuilder.min.css\' );\n                foreach ( $styles as $style ) {\n                if ( ! $style ) continue;\n                $style = DIR_TEMPLATE . $theme . \'/stylesheet/\' . $style;\n                $file = false;\n                if ( file_exists( $style . \'.min.css\' ) ) {\n                $file = $style . \'.min.css\';\n                } else if ( file_exists( $style . \'.css\' ) ) {\n                $file = $style . \'.css\';\n                }\n                if ( $file ) {\n                $file = str_replace( DIR_APPLICATION, basename( DIR_APPLICATION ) . \'/\', $file );\n                $this->document->addStyle( $file );\n                }\n                }\n\n                $google_api_key = $this->config->get( \'pavothemer_google_map_api_key\' );\n                $this->document->addScript( \'//maps.googleapis.com/maps/api/js?key=\' .$google_api_key );\n                /**\n                * script files\n                *\n                * common script\n                * customize script\n                */\n                $coreScripts = array(\n                \'pavobuilder/dist/pavobuilder.min.js\',\n                \'pavothemer/dist/pavothemer.min.js\'\n                );\n\n                foreach ( $coreScripts as $script ) {\n                $file = dirname ( DIR_TEMPLATE ) . \'/javascript/\' . $script;\n                if ( file_exists( $file ) ) {\n                $file = str_replace( DIR_APPLICATION, basename( DIR_APPLICATION ) . \'/\', $file );\n                $this->document->addScript( $file );\n                }\n                }\n                $scripts = array(\n                \'common\',\n                \'customize\'\n                );\n\n                foreach ( $scripts as $script ) {\n                $script = DIR_TEMPLATE . $theme . \'/javascript/\' . $script;\n                $file = false;\n\n                if ( file_exists( $script . \'.min.js\' ) ) {\n                $file = $script . \'.min.js\';\n                } else if ( file_exists( $script . \'.js\' ) ) {\n                $file = $script . \'.js\';\n                }\n\n                if ( $file ) {\n                $file = str_replace( DIR_APPLICATION, basename( DIR_APPLICATION ) . \'/\', $file );\n                $this->document->addScript( $file );\n                }\n                }]]></add>\n            </operation>\n        </file>\n    </modification>', 1, '2017-12-23 08:56:42'),
(59, 80, 'Pavo Megamenu', 'pavmegamenu', 'pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<name>Pavo Megamenu</name>\r\n	<code>pavmegamenu</code>\r\n	<version>1.0.0</version>\r\n	<author>pavothemes</author>\r\n	<link>http://www.pavothemes.com</link>\r\n    <file path=\"catalog/controller/common/header.php\">\r\n        <operation>\r\n            <search><![CDATA[$data[\'menu\'] = $this->load->controller(\'common/menu\');]]></search>\r\n            <add position=\"after\"><![CDATA[\r\n                //Call Pav megamenu module to display\r\n                $data[\'pavmegamenu\'] = $this->load->controller(\'extension/module/pavmegamenu\');\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"catalog/view/theme/*/template/common/header.twig\">\r\n        <operation>\r\n            <search><![CDATA[{{ menu }}]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                {{ pavmegamenu }}\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"admin/controller/common/column_left.php\">\r\n        <operation>\r\n            <search><![CDATA[// Catalog]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                // pavmegamenu language file\r\n                $this->load->language(\'extension/module/pavmegamenu\');\r\n\r\n                // pavmegamenu Settings menu\r\n                $settings = array();\r\n                // view settings panel\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavmegamenu\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'text_megamenu_menus\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavmegamenu\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                if ( $settings ) {\r\n                    $data[\'menus\'][] = array(\r\n                        \'id\'       => \'pavmegamenu\',\r\n                        \'icon\'     => \'fa fa-bars\',\r\n                        \'name\'     => $this->language->get( \'text_megamenu_menus\' ),\r\n                        \'href\'     => \'\',\r\n                        \'children\' => $settings\r\n                    );\r\n                }\r\n                // End pavmegamenu Settings menu\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    \r\n</modification>', 1, '2017-12-08 10:16:44'),
(26, 47, 'Pavo Layered Navigation', 'pavlayerednavigation', 'Pavotheme.Com', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Pavo Layered Navigation</name>\n	<code>pavlayerednavigation</code>\n	<version>1.0.0</version>\n	<author>Pavotheme.Com</author>\n	<link>http://www.pavothemes.com</link>\n\n    <file path=\"catalog/model/catalog/product.php\">\n        <operation>\n            <search><![CDATA[if (!empty($data[\'filter_manufacturer_id\'])) {]]></search>\n            <add position=\"before\">\n                <![CDATA[\n        /* Price range */\n        if  (!empty($data[\'filter_price\'])) {\n            $min_price = $data[\'filter_price\'][\'min_price\'];\n            $max_price = $data[\'filter_price\'][\'max_price\'];\n            $sql_filter_special = \"(SELECT price FROM \" . DB_PREFIX . \"product_special ps WHERE ps.product_id = p.product_id AND ps.customer_group_id = \'\" . (int)$this->config->get(\'config_customer_group_id\') . \"\' AND ((ps.date_start = \'0000-00-00\' OR ps.date_start < NOW()) AND (ps.date_end = \'0000-00-00\' OR ps.date_end > NOW())) ORDER BY ps.priority ASC, ps.price ASC LIMIT 1)\";\n            $sql_sl_discount = \"(SELECT price FROM \" . DB_PREFIX . \"product_discount pd2 WHERE pd2.product_id = p.product_id AND pd2.customer_group_id = \'\" . (int)$this->config->get(\'config_customer_group_id\') . \"\' AND pd2.quantity = \'1\' AND ((pd2.date_start = \'0000-00-00\' OR pd2.date_start < NOW()) AND (pd2.date_end = \'0000-00-00\' OR pd2.date_end > NOW())) ORDER BY pd2.priority ASC, pd2.price ASC LIMIT 1)\";\n            $sql .= \" AND (CASE WHEN \" . $sql_filter_special . \" IS NOT NULL THEN \" . $sql_filter_special . \" WHEN \" . $sql_sl_discount . \" IS NOT NULL THEN \". $sql_sl_discount . \" ELSE p.price END) >=\'\". $min_price .\"\'\" ;\n            $sql .= \" AND (CASE WHEN \" . $sql_filter_special . \" IS NOT NULL THEN \" . $sql_filter_special . \" WHEN \" . $sql_sl_discount . \" IS NOT NULL THEN \". $sql_sl_discount . \" ELSE p.price END) <=\'\". $max_price .\"\'\";\n        }\n        /* End */\n                ]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"catalog/view/theme/*/template/product/category.twig\">\n        <operation>\n            <search><![CDATA[{% if products %}]]></search>\n            <add position=\"before\">\n                <![CDATA[\n                <div class=\"products-collection\">\n            ]]>\n            </add>\n        </operation>  \n         <operation>\n            <search><![CDATA[ {% if not categories and not products %}]]></search>\n            <add position=\"before\">\n                <![CDATA[\n               </div>\n            ]]>\n            </add>\n        </operation>    \n    </file>\n</modification>', 1, '2017-11-10 13:54:29'),
(32, 53, 'Pavo User Popup', 'pavuserpopup', 'Pavothemes', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Pavo User Popup</name>\n	<code>pavuserpopup</code>\n	<version>1.0.0</version>\n	<author>Pavothemes</author>\n	<link>http://www.pavothemes.com</link>\n 	\n 	<file path=\"catalog/controller/common/footer.php\">\n        <operation>\n            <search><![CDATA[$data[\'contact\'] = $this->url->link(\'information/contact\');]]></search>\n            <add position=\"before\">\n                <![CDATA[\n               $data[\'user_popup\'] = $this->load->controller(\'extension/module/pavuserpopup\');\n            ]]>\n            </add>\n        </operation>     \n    </file>\n\n    <file path=\"catalog/view/theme/*/template/common/footer.twig\">\n        <operation>\n            <search><![CDATA[</body>]]></search>\n            <add position=\"before\">\n                <![CDATA[\n               {{ user_popup }}\n            ]]>\n            </add>\n        </operation>     \n    </file>\n\n</modification>', 1, '2017-11-16 14:46:18'),
(31, 52, 'Pavo Store Locator', 'pavstorelocator', 'Pavotheme.Com', '1.0.0', 'http://www.pavothemes.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<name>Pavo Store Locator</name>\n	<code>pavstorelocator</code>\n	<version>1.0.0</version>\n	<author>Pavotheme.Com</author>\n	<link>http://www.pavothemes.com</link>\n	<file path=\"admin/view/template/localisation/location_form.twig\">\n        <operation>\n            <search><![CDATA[ </form>]]></search>\n            <add position=\"before\"><![CDATA[\n					     <div class=\"form-group\">\n				            <label class=\"col-sm-2 control-label\" for=\"input-email\">{{ entry_email }}</label>\n				            <div class=\"col-sm-10\">\n				              <input type=\"text\" name=\"email\" value=\"{{ email }}\" placeholder=\"{{ entry_email }}\" id=\"input-email\" class=\"form-control\" />\n				            </div>\n				          </div>   \n				          <div class=\"form-group\">\n				            <label class=\"col-sm-2 control-label\" for=\"input-video\">{{ entry_video }}</label>\n				            <div class=\"col-sm-10\">\n				              <input type=\"text\" name=\"video\" value=\"{{ video }}\" placeholder=\"{{ entry_video }}\" id=\"input-video\" class=\"form-control\" />\n				            </div>\n				          </div>   \n\n				             <div class=\"table-responsive form-group\">\n				               <label class=\"col-sm-2 control-label\" for=\"input-gallery\">{{ entry_gallery }}</label>\n				                <div class=\"col-sm-10\">\n				                  <table id=\"images\" class=\"table table-striped table-bordered table-hover\">\n				                    <thead>\n				                      <tr>\n				                        <td class=\"text-left\">{{ entry_additional_image }}</td>\n				                        <td class=\"text-right\">{{ entry_sort_order }}</td>\n				                        <td></td>\n				                      </tr>\n				                    </thead>\n				                    <tbody>\n				                    \n				                    {% set image_row = 0 %}\n				                    {% for image in images %}\n				                    <tr id=\"image-row{{ image_row }}\">\n				                      <td class=\"text-left\"><a href=\"\" id=\"thumb-image{{ image_row }}\" data-toggle=\"image\" class=\"img-thumbnail\"><img src=\"{{ catalog_url }}/image/{{ images[image_row] }}\" alt=\"\" title=\"\" data-placeholder=\"{{ placeholder }}\" /></a>\n				                        <input type=\"hidden\" name=\"images[{{ image_row }}]\" value=\"{{ images[image_row] }}\" id=\"input-image{{ image_row }}\" /></td>\n				                   \n				                      <td class=\"text-left\"><button type=\"button\" onclick=\"$(\'#image-row{{ image_row }}\').remove();\" data-toggle=\"tooltip\" title=\"{{ button_remove }}\" class=\"btn btn-danger\"><i class=\"fa fa-minus-circle\"></i></button></td>\n				                    </tr>\n				                    {% set image_row = image_row + 1 %}\n				                    {% endfor %}\n				                      </tbody>\n				                    \n				                    <tfoot>\n				                      <tr>\n				                        <td colspan=\"2\"></td>\n				                        <td class=\"text-left\"><button type=\"button\" onclick=\"addImage();\" data-toggle=\"tooltip\" title=\"{{ button_image_add }}\" class=\"btn btn-primary\"><i class=\"fa fa-plus-circle\"></i></button></td>\n				                      </tr>\n				                    </tfoot>\n				                  </table>\n				                </div>\n				              </div>\n\n				           <script type=\"text/javascript\">\n				            var image_row = {{ image_row }};\n\n				            function addImage() {\n				              html  = \'<tr id=\"image-row\' + image_row + \'\">\';\n				              html += \'  <td class=\"text-left\"><a href=\"\" id=\"thumb-image\' + image_row + \'\"data-toggle=\"image\" class=\"img-thumbnail\"><img src=\"{{ placeholder }}\" alt=\"\" title=\"\" data-placeholder=\"{{ placeholder }}\" /></a><input type=\"hidden\" name=\"images[\' + image_row + \']\" value=\"\" id=\"input-image\' + image_row + \'\" /></td>\';\n				             \n				              html += \'  <td class=\"text-left\"><button type=\"button\" onclick=\"$(\\\'#image-row\' + image_row  + \'\\\').remove();\" data-toggle=\"tooltip\" title=\"{{ button_remove }}\" class=\"btn btn-danger\"><i class=\"fa fa-minus-circle\"></i></button></td>\';\n				              html += \'</tr>\';\n\n				              $(\'#images tbody\').append(html);\n\n				              image_row++;\n				            }\n				            </script> \n            ]]></add>\n        </operation>\n    </file>\n     \n     <file path=\"admin/controller/localisation/location.php\">\n\n        <operation>\n            <search><![CDATA[$this->model_localisation_location->addLocation($this->request->post); ]]></search>\n            <add position=\"replace\">\n            	<![CDATA[ $id = $this->model_localisation_location->addLocation($this->request->post);\n            			 $this->load->model(\'extension/pavstorelocator/location\');\n						 $this->model_extension_pavstorelocator_location->edit( $id, $this->request->post );\n            	]]>\n            </add>\n        </operation>    \n        <operation>\n            <search><![CDATA[ 	$this->load->model(\'tool/image\'); ]]></search>\n            <add position=\"before\"><![CDATA[\n				if (isset($this->request->post[\'video\'])) {\n					$data[\'video\'] = $this->request->post[\'video\'];\n				} elseif (!empty($location_info)) {\n					$data[\'video\'] = $location_info[\'video\'];\n				} else {\n					$data[\'video\'] = \'\';\n				}\n\n				if (isset($this->request->post[\'email\'])) {\n					$data[\'email\'] = $this->request->post[\'email\'];\n				} elseif (!empty($location_info)) {\n					$data[\'email\'] = $location_info[\'email\'];\n				} else {\n					$data[\'email\'] = \'\';\n				}\n\n\n				$data[\'catalog_url\'] = HTTP_CATALOG;\n				if (isset($this->request->post[\'email\'])) {\n					$data[\'images\'] = $this->request->post[\'images\'];\n				} elseif (!empty($location_info)) {\n					$data[\'images\'] = explode( \"|\", $location_info[\'gallery\'] );\n				} else {\n					$data[\'images\'] = \'\';\n				}\n            ]]></add>\n        </operation>\n        <operation>\n            <search><![CDATA[ $this->model_localisation_location->editLocation($this->request->get[\'location_id\'], $this->request->post); ]]></search>\n            <add position=\"after\"><![CDATA[\n            	$this->load->model(\'extension/pavstorelocator/location\');\n				$this->model_extension_pavstorelocator_location->edit( $this->request->get[\'location_id\'], $this->request->post );\n            ]]></add>\n        </operation>    \n    </file>\n\n      <file path=\"admin/language/*/localisation/location.php\">\n     	\n        <operation>\n            <search><![CDATA[$_[\'entry_name\']       = \'Store Name\'; ]]></search>\n            <add position=\"replace\">\n            	<![CDATA[ \n            		$_[\'entry_email\']       = \'Email\';\n            		$_[\'entry_video\']       = \'Video\';\n            		$_[\'entry_gallery\']       = \'Gallery\';\n            	]]>\n            </add>\n        </operation>    \n    </file>    \n</modification>', 1, '2017-11-16 13:35:14'),
